import { motion } from "framer-motion"
import React from "react"



const RenderPokemonAbility = (abilityData) => {
    const effectEntries = abilityData.effect_entries;
    return (
        <motion.div className='ability-container'>
            {effectEntries.map((effectData) => (
                (effectData.language.name === "en") ? (
                    <p>{effectData.short_effect}</p>
                ) : (
                    <></>
                )
            ))}
        </motion.div>

    )
}

export default RenderPokemonAbility;
