import { motion } from "framer-motion"
import React, { useState } from "react"
import './PokemonAbilities.css'
import RenderPokemonAbilityBox from "./PokemonAbilityBox";

export const getAbilityName = (ability) => {
    let name = "";
    ability.names.map((nameData) => {
        if(nameData.language.name === "en") {
            name = nameData.name;
        }
    })
    return name;
}

const RenderPokemonAbilityButton = (props) => {
    const [abilityToDisplay, setAbilityToDisplay] = useState([]);
    const abilityData = props.abilityData;
    
    const buttonAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '-100vh',
          opacity: 0,
        }
    }

    const effectEntries = abilityData.effect_entries;
    return (
      <>
        <div className='ability-button-container'>
            {effectEntries.map((effectData) => (
                <motion.div className='ability-button' variants={buttonAnim} initial='before' animate='onScreen' exit='after' whileTap={{scale: 0.8}} onClick={() => props.updateCall(abilityData.ability)}>
                  {(effectData.language.name === "en") ? (
                    <>
                      <p>{getAbilityName(abilityData)}</p>
                    </>
                  ) : (
                    <></>
                  )}
                </motion.div>
            ))}
        </div>
        {(abilityToDisplay.length === 0) ? (
            <></>
        ) : (
            <RenderPokemonAbilityBox ability={abilityToDisplay} />
        )}
      </>
    )
}

export default RenderPokemonAbilityButton;
