const getHeightComparison = (props) => {
    const pokemonHeight = props.height;
    const ashHeight = 140;
    let provisionalPokemonHeight = pokemonHeight * 3;
    let provisionalAshHeight = ashHeight * 3;
    if(provisionalAshHeight > provisionalPokemonHeight) {
        const difference = provisionalAshHeight - 300;
        const ratio = difference / provisionalAshHeight;
        console.log(difference, ratio)
        provisionalPokemonHeight = Math.round(provisionalPokemonHeight * ratio);
        provisionalAshHeight = 300;
    } else {
        const difference = provisionalPokemonHeight - 300;
        const ratio = difference / provisionalPokemonHeight;
        console.log(difference, ratio)
        provisionalAshHeight = Math.round(provisionalAshHeight * ratio);
        provisionalPokemonHeight = 300;
    }
    return {pHeight: provisionalPokemonHeight, aHeight: provisionalAshHeight};
}

export default getHeightComparison;
