import React from "react";
import getGradient from "../GetGradient";

const PokemonTypeButton = (props) => {
    const gradientString = getGradient({typeString: props.typeString});
    if(props.glow) {
      return (
        <button style={{
          backgroundImage: "linear-gradient(120deg, " + gradientString + ")",
          borderRadius: "5px",
          height: '30px',
          width: '80px',
          textTransform: 'capitalize'}} value={props.typeString}>
            {props.typeString}
        </button>
      )  
    } else {
      return (
        <button style={{
          backgroundImage: "linear-gradient(120deg, " + gradientString + ")",
          borderRadius: "5px",
          height: '30px',
          width: '80px',
          textTransform: 'capitalize',
          boxShadow: '0 5px 15px rgba(' + darkerColour + ', .8)'}} value={props.typeString}>
            {props.typeString}
        </button>
      )
    }
}

export default PokemonTypeButton;
