import React, { useState, useEffect } from "react";
import PokemonTypeButton from '../PokemonTypeButton/PokemonTypeButton.js';
import RenderPokemonAbilityButton from "../PokemonAbilities/PokemonAbilities.js";
import RenderPokemonAbilityBox from "../PokemonAbilities/PokemonAbilityBox.js";
import RenderExpandableType from "../ExpandableButtons/RenderExpandableType.js";
import RenderExpandableGrowth from "../ExpandableButtons/RenderExpandableGrowth.js";
import './PokemonInfo.css'
import getGradient from "../GetGradient.js";
import { AnimatePresence, motion } from 'framer-motion';
import RenderBasicInfo from "./PokemonBasicInfoContainer.js";

const getAbilityData = async (abilityName) => {
  try {
    const res = await fetch("https://pokeapi.co/api/v2/ability/" + abilityName);
    const pokemonPromise = await res.json();
    return pokemonPromise;
  } catch (e) {
    console.log(e);
  }
};

const getSpeciesData = async (pokeId) => {
  try {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon-species/" + pokeId);
    const speciesPromise = await res.json();
    return speciesPromise;
  } catch (e) {
    console.log(e);
  }
}

const getGrowthData = async (url) => {
  try {
    const res = await fetch(url);
    const growthPromise = await res.json();
    return growthPromise;
  } catch (e) {
    console.log(e);
  }
}

const getTypeData = async (url) => {
  try {
    const res = await fetch(url);
    const typePromise = await res.json();
    return typePromise;
  } catch (e) {
    console.log(e);
  }
}


const RenderPokemonInfo = (props) => {
    const [abilities, setAbilities] = useState([]);
    const [abilityToDisplay, setAbilityToDisplay] = useState([]);
    const [growthData, setGrowthData] = useState([]);
    const [speciesData, setSpeciesData] = useState([]);
    const [typeData, setTypeData] = useState([]);
    const pokemon = props.pokemonToUse;
    const pokemonCurrentTypes = [];
    let gradientString = "";
    console.log(pokemon)

    useEffect(() => {
      const abilityPromises = [];
      pokemon.abilities.map((abilityData) => {
        abilityPromises.push(getAbilityData(abilityData.ability.name));
      })
      Promise.all(abilityPromises).then((results) => {
        setAbilities(results);
      })
      const speciesPromises = [];
      speciesPromises.push(getSpeciesData(pokemon.id));
      Promise.all(speciesPromises).then((results) => {
        setSpeciesData(results[0])
      })
      const typePromises = [];
      pokemon.types.map((typeData) => {
        typePromises.push(getTypeData(typeData.type.url))
      })
      Promise.all(typePromises).then((results) => {
        setTypeData(results)
      })
    }, []);

    useEffect(() => {
      const growthPromises = [];
      if(speciesData.length !== 0) {
        growthPromises.push(getGrowthData(speciesData.growth_rate.url));
        Promise.all(growthPromises).then((results)=>{
          setGrowthData(results[0]);
        })
      }
    }, [speciesData])
    
    const updateAbilityToDisplay = (ability) => {
      if(abilityToDisplay === ability) {
        setAbilityToDisplay([]);
      } else {
        setAbilityToDisplay(ability);
      }
    }

    const animateFromTop = {
      before: {
        y: '-100vh',
        opacity: 0,
      },
      onScreen: {
        y: '0',
        opacity: 1,
      },
      after: {
        y: '100vh',
        opacity: 0,
      }
    }



    pokemon.types.map((typeData) => {
        pokemonCurrentTypes.push(typeData.type.name);
    })

    if(pokemonCurrentTypes.length === 1) {
      gradientString = getGradient({typeString: pokemonCurrentTypes[0]});
    } else {
      gradientString = getGradient({typeString: pokemonCurrentTypes[0]}) + ', ' + getGradient({typeString: pokemonCurrentTypes[1]});
    }
    console.log(typeData)
    return (
      <>
        <motion.div style={{
          position: 'absolute',
          width: '420px',
          height: '640px',
          left: '75%',
          top: '30px',
          transform: 'translateX(-50%)',
          backgroundImage: 'linear-gradient(225deg, ' + gradientString + ')',
          boxShadow: '0 20px 20px 0 rgba(0,0,0,0.2)',
          borderRadius: '30px'}} key={pokemon.name} variants={animateFromTop} initial='before' animate='onScreen' exit='after'>
          <fieldset className="poke-height-fieldset">
            <legend style={{position: 'absolute', left: '10px'}}>{pokemon.height * 10} cm</legend>
            <div className='big-poke-img-container'>
              <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + pokemon.id + '.svg'} className='big-poke-img' key={pokemon.name}></img>
            </div>
          </fieldset>
          <div className='poke-type-container'>
              {pokemon.types.map((type) => React.Children.toArray(<PokemonTypeButton typeString={type.type.name} isButton={false}/>))}
          </div>
          <p className='ability-text'> Abilities: </p>
          <div className='ability-container'>
            {React.Children.toArray(abilities.map((abilityData) => (
              <RenderPokemonAbilityButton abilityData={abilityData} updateAbilityToDisplay={updateAbilityToDisplay} />
            )))}
          </div>
          <AnimatePresence exitBeforeEnter={true}>
            {(abilityToDisplay.length === 0) ? (
                <></>
              ) : (
                  <RenderPokemonAbilityBox ability={abilityToDisplay} key={abilityToDisplay}/>
              )}
          </AnimatePresence>
        </motion.div>
        <motion.div className='stats-container' variants={animateFromTop} initial='before' animate='onScreen' exit='before'>
          <h1>Base Stats:</h1>
          {React.Children.toArray(pokemon.stats.map((statData) => (
            <p>{statData.stat.name.replace('-', ' ')} - {statData.base_stat}</p>
          )))}
          <p>Base Exp - {pokemon.base_experience}</p>
        </motion.div>
        {(speciesData.length === 0) ? (
          <></>
        ) : (
          <RenderBasicInfo pokemonToUse={pokemon} speciesToUse={speciesData} />
        )}
        {(growthData.length === 0) ? (
          <></>
        ) : (
          <RenderExpandableGrowth growthToUse={growthData} />
        )}
        {(typeData.length === 0) ? (
          <></>
        ) : (
          <div className='matchup-container'>
            {typeData.map((type) => (
              <RenderExpandableType typeToUse={type} />
            ))}
          </div>
        )}
      </>
    )
}

export default RenderPokemonInfo;


/*
      <div style={{
        height: '600px',
        width: '420px',
        background: 'linear-gradient()',
      }}>

*/
