import React from "react";
import { motion } from "framer-motion";

const RenderBasicInfo = (props) => {


    const pokemon = props.pokemonToUse;
    const speciesData = props.speciesToUse;

    const animateFromTop = {
        before: {
          y: '-100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }

    return (
      <motion.div className='basic-info-container' variants={animateFromTop} initial='before' animate='onScreen' exit='before'>
        <h1>Info:</h1>
        <p>Weight - {pokemon.weight}</p>
        <p>Base Happiness - {speciesData.base_happiness}</p>
        <p>Capture Rate - {speciesData.capture_rate}</p>
        {speciesData.genera.map((genusData) => (
          (genusData.language.name === "en") ? (
            <p>Genus - {genusData.genus}</p>
          ) : (
            <></>
        )))}
        <p>Habitat - {speciesData.habitat.name}</p>
        <p>Egg Groups: </p>
        {speciesData.egg_groups.map((eggGroupData) => (
          <p>{eggGroupData.name}</p>
        ))}
        <p></p>
      </motion.div>
    )
}

export default RenderBasicInfo;
