import React from "react";
import PokemonTypeButton from '../PokemonTypeButton/PokemonTypeButton.js';
import './PokemonInfo.css'

const RenderPokemonInfo = (props) => {
    const pokemon = props.pokemon;
    const height = props.pokemon.height * 10;
    console.log(height);
    return (
        <div className='picture-container'>
          <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + pokemon.id + '.svg'} className='big-poke-img'></img>
          <img src={'https://www.pngmart.com/files/12/Pokemon-Ash-Ketchum-PNG-Free-Download.png'} className='human-img'></img>
          <div className='poke-type-container'>
            {pokemon.types.map((type) => <PokemonTypeButton typeString={type.type.name} />)}
          </div>
        </div>
    )
}

export default RenderPokemonInfo;


