import React, { useState } from "react";
import { motion } from "framer-motion";

const RenderExpandableType = (props) => {

    const [expanded, setExpanded] = useState(false);

    const handleClick = () => {
      setExpanded(!expanded)
    }

    const type = props.typeToUse;
    const damageRelations = type.damage_relations;

    const typeExpand = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        collapsed: {
          y: '0',
          width: 300,
          height: 80,
          opacity: 1,
        },
        expanded: {
          y: '0',
          width: 450,
          height: 800,
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }

    console.log(type)
    return (
      <motion.div className='matchup-button' variants={typeExpand} initial='before' animate={(expanded ? 'expanded' : 'collapsed')} exit='after' onClick={handleClick}>
        <h1>View {type.name} Damage Relations:</h1>
        {(damageRelations.double_damage_from.length === 0) ? (
            <></>
        ) : (
            React.Children.toArray(damageRelations.double_damage_from.map((doubleDamageData) => (
                <li>{doubleDamageData.name}</li>
            )))
        )}

      </motion.div>
    )
}

export default RenderExpandableType;
