import React from "react"
import { motion } from "framer-motion"

const RenderBasicStats = (props) => {

    const pokemon = props.pokemonToUse;

    const animateFromTop = {
        before: {
          y: '-100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }

    return (
        <motion.div className='stats-container' variants={animateFromTop} initial='before' animate='onScreen' exit='before'>
          <h1>Base Stats:</h1>
          {React.Children.toArray(pokemon.stats.map((statData) => (
            <p>{statData.stat.name.replace('-', ' ')} - {statData.base_stat}</p>
          )))}
          <p>Base Exp - {pokemon.base_experience}</p>
        </motion.div>
    )
}

export default RenderBasicStats;
