import React from "react";
import PokeColour from "../TypeEnum.ts";
import './PokemonCard.css'
import PokemonTypeButton from "../PokemonTypeButton/PokemonTypeButton.js";


const PokemonCard = (props) => {
    const colours = [];
    const pokemonCurrentTypes = []
    let gradientString = "";

    props.pokemonToUse.types.map((typeData) => {
        colours.push(PokeColour[typeData.type.name]);
        pokemonCurrentTypes.push(typeData.type.name);
    })

    if(colours.length === 1) {
        const colourRGB = [colours[0].slice(1, 3), colours[0].slice(3, 5), colours[0].slice(5, 7)];
        const lighterColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 1.4))).join();
        const darkerColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 0.8))).join();
        gradientString = 'rgb(' + lighterColour + ')' + ', rgb(' + darkerColour + ')';
    } else {
        gradientString = colours[0] + ', ' + colours[1];
    }

    return (
      <>
        <div className='card' id={props.pokemonToUse.id}>
          <div style={{
            backgroundImage: "linear-gradient(120deg, " + gradientString + ")",
            height: '200px',
            borderRadius: '5px'}} id={props.pokemonToUse.id} onClick={() => props.updateIdCall(props.pokemonToUse.id)}>
            <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + props.pokemonToUse.id + '.svg'} className='poke-img' id={props.pokemonToUse.id}/>
            <div className='card-info-container'>
              <p id={props.pokemonToUse.id}>{props.pokemonToUse.name}</p>
            </div>
            <div className='card-button-container'>
              {pokemonCurrentTypes.map((typeData) => (
                <PokemonTypeButton typeString={typeData}></PokemonTypeButton>
              ))}
            </div>
          </div>
        </div>
      </>
    )
}

export default PokemonCard;
