import React, { useEffect, useState } from 'react';
import './PokemonRender.css'
import ReactDOM from 'react-dom';

const PokemonRender = (text) => {
  const [rawPokemonList, setRawPokemonList] = useState([]);
  const [sortedPokemonList, setSortedPokemonList] = useState([]);
  console.log(text)
  console.log(text.input)
  const getPokemonData = async (id) => {
    //Move this out somehow, to avoid calling on every render 
    try {
      const res = await fetch("https://pokeapi.co/api/v2/pokemon/" + id);
      const pokemon = await res.json();
      return pokemon;
    } catch (e) {
      console.log(e);
    }
  };

  useEffect((i) => {
    const promises = [];
    const sortingList = [];
    for (let i = 1; i < 152; i++) {
      promises.push(getPokemonData(i));
    }
    Promise.all(promises).then((results)=>{
      setRawPokemonList(results);
    })
    rawPokemonList.map(poke => {
      if(poke.name.includes(text.input)) {
        console.log(1);
        sortingList.push(poke);
      }
      if(text.input === "") {
        console.log(2);
        sortingList.push(poke);
      }
    })
    setSortedPokemonList(sortingList);
    console.log(sortedPokemonList);
  }, []);

  return (
      sortedPokemonList.map(poke=>(
        <div className='card'>
          <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + poke.id + '.svg'} className='PokeImg' key={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + poke.id + '.svg'}/>
          <div className='container'>
            <p>{poke.name}</p>
          </div>
        </div>
      ))
  )
}

export default PokemonRender;
