import { motion } from "framer-motion"
import React, { useState } from "react"
import './PokemonAbilities.css'
import RenderPokemonAbilityBox from "./PokemonAbilityBox";

export const getAbilityName = (ability) => {
    let name = "";
    ability.names.map((nameData) => {
        if(nameData.language.name === "en") {
            name = nameData.name;
        }
    })
    return name;
}

const RenderPokemonAbility = (abilityData) => {

    const [clicked, setClicked] = useState(false);

    const buttonAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '-100vh',
          opacity: 0,
        }
    }

    const effectEntries = abilityData.ability.effect_entries;
    return (
      <>
        <motion.div className='ability-button' variants={buttonAnim} initial='before' animate='onScreen' exit='after' whileTap={{scale: 0.8}} onClick={() => setClicked(!clicked)}>
            {effectEntries.map((effectData) => (
                (effectData.language.name === "en") ? (
                    <>
                      <p>{getAbilityName(abilityData.ability)}</p>
                    </>
                ) : (
                    <></>
                )
            ))}
        </motion.div>
        (clicked) ? (
            <RenderPokemonAbilityBox ability={abilityData} />
        ) : (
            <></>
        )
      </>
    )
}

export default RenderPokemonAbility;
