import React, { useEffect, useState } from 'react';
import './PokemonCards.css'
import PokeColour from './TypeEnum.ts'

const RenderPokemon = (text) => {
  const [rawPokemonList, setRawPokemonList] = useState([]);
  const [[pokemonId, pokemon], setPokemon] = useState([0, []]);
  const pokemonTypeList = ['grass', 'poison', 'fire', 'flying', 'water', 'bug', 'normal', 'electric', 'ground', 'fairy', 'fighting', 'psychic', 'rock', 'steel', 'ice', 'ghost', 'dragon'];

  const getPokemonData = async (id) => {
    //Move this out somehow, to avoid calling on every render 
    try {
      const res = await fetch("https://pokeapi.co/api/v2/pokemon/" + id);
      const pokemonPromise = await res.json();
      return pokemonPromise;
    } catch (e) {
      console.log(e);
    }
  };

  const sortPokemonList = rawPokemonList.filter((poke) => {
    if(text.input === "") {
      return poke;
    } else {
      return poke.name.toLowerCase().includes(text.input);
    }
  })

  const updatePokemonId = (e) => {
    console.log(e.target.id);
    rawPokemonList.map(poke => {
      if(poke.id === e.target.id) {
        setPokemon([e.target.id, poke]);
      }
    })
    console.log(pokemonId);
  }

  useEffect((i) => {
    const promises = [];
    for (let i = 1; i < 152; i++) {
      promises.push(getPokemonData(i));
    }
    Promise.all(promises).then((results)=>{
      setRawPokemonList(results);
    })
  }, []);

  const PokemonTypeButton = (props) => {
    const typeColour = PokeColour[props.typeString];
    const colourRGB = [typeColour.slice(1, 3), typeColour.slice(3, 5), typeColour.slice(5, 7)];
    const lighterColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 1.4))).join();
    const darkerColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 0.6))).join();
    let colour = 'rgb(0,0,0)';
    if((Math.round(parseInt(colourRGB[2]) * 0.6) < 70)) {
      colour = 'rgb(255,255,255)';
    }
    const gradientString = 'rgb(' + lighterColour + ')' + ', rgb(' + darkerColour + ')';
    return (
      <button style={{
        backgroundImage: "linear-gradient(120deg, " + gradientString + ")",
        borderRadius: "5px",
        height: '30px',
        width: '80px',
        color: colour}}>
          {props.typeString}
      </button>
    )
  }

  const PokemonCard = (props) => {
    const colours = [];
    let gradientString = "";
    props.pokemonToUse.types.map((typeData) => (
      colours.push(PokeColour[typeData.type.name])
    ))
    if(colours.length === 1) {
      const colourRGB = [colours[0].slice(1, 3), colours[0].slice(3, 5), colours[0].slice(5, 7)];
      const lighterColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 1.4))).join();
      const darkerColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 0.6))).join();
      gradientString = 'rgb(' + lighterColour + ')' + ', rgb(' + darkerColour + ')';
    } else {
      gradientString = colours[0] + ', ' + colours[1];
    }
    return (
      <div style={{
        backgroundImage: "linear-gradient(120deg, " + gradientString + ")",
        height: '200px',
        borderRadius: '5px'}} id={props.pokemonToUse.id} onClick={updatePokemonId}>
        <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + props.pokemonToUse.id + '.svg'} className='poke-img' id={props.pokemonToUse.id}/>
        <div className='card-text-container'>
          <p id={props.pokemonToUse.id}>{props.pokemonToUse.name}</p>
        </div>
      </div>
    )
  }


  return (
    <>
    <div className='content-parent'>
      <div className='card-container'>
        {sortPokemonList.map(poke=>(
          <div className='card' id={poke.id}>
            <PokemonCard pokemonToUse={poke} id={poke.id} />
          </div>
        ))}
      </div>
      <div className='content-container'>
        {(pokemonId === 0) ? (
          <p>Click a pokemon!</p>
        ) : (
          <>
          <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + pokemonId + '.svg'} className='big-poke-img'></img>
          <p>Name - {pokemon.name}</p>
          </>
        )}
      </div>
    </div>
    <div className='type-container'>
      {pokemonTypeList.map((type) => (
        <div className='type-button'>
          <PokemonTypeButton typeString={type}></PokemonTypeButton>
        </div>
        ))}
    </div>
    </>
  )
}

export default RenderPokemon;
