import React from "react";
import { motion } from "framer-motion";
import './BasicInfo.css'

const RenderBasicInfo = (props) => {


    const pokemon = props.pokemonToUse;
    const speciesData = props.speciesToUse;

    const animateFromTop = {
        before: {
          y: '-100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }
    
    return (
      <motion.div className='basic-info-container' variants={animateFromTop} initial='before' animate='onScreen' exit='before'>
        <h1>Info:</h1>
        <div className='left-container'>
          <p>Weight</p>
          <p>Base Happiness</p>
          <p>Capture Rate</p>
          {React.Children.toArray(speciesData.genera.map((genusData) => (
            (genusData.language.name === "en") ? (
              <p>Genus</p>
            ) : (
              <></>
          ))))}
          <p>Habitat</p>
          <p>Egg Groups: </p>
        </div>
        <div className='right-container'>
          <p>{pokemon.weight}</p>
          <p>{speciesData.base_happiness}</p>
          <p>{speciesData.capture_rate}</p>
          {React.Children.toArray(speciesData.genera.map((genusData) => (
            (genusData.language.name === "en") ? (
              <p>{genusData.genus}</p>
            ) : (
              <></>
          ))))}
          <p>{speciesData.habitat.name}</p>
          <p>{React.Children.toArray(speciesData.egg_groups.map((eggGroupData) => (
            eggGroupData.name + '\u00A0'
          )))}</p>
        </div>
      </motion.div>
    )
}

export default RenderBasicInfo;
