import React, { useEffect, useState } from 'react';
import './RenderPokemon.css'
import RenderPokemonInfo from '../PokemonInfo/PokemonInfo'
import PokemonCard from '../PokemonCard/PokemonCard.js'
import PokemonTypeButton from '../PokemonTypeButton/PokemonTypeButton.js';


const getPokemonData = async (id) => {
  try {
    const res = await fetch("https://pokeapi.co/api/v2/pokemon/" + id);
    const pokemonPromise = await res.json();
    return pokemonPromise;
  } catch (e) {
    console.log(e);
  }
};

const RenderPokemon = (text) => {
  const [rawPokemonList, setRawPokemonList] = useState([]);
  const [[pokemonId, pokemon], setPokemon] = useState([0, []]);
  const pokemonTypeList = ['grass', 'poison', 'fire', 'flying', 'water', 'bug', 'normal', 'electric', 'ground', 'fairy', 'fighting', 'psychic', 'rock', 'steel', 'ice', 'ghost', 'dragon'];
  const [typesToDisplay, setTypesToDisplay] = useState([]);

  const sortPokemonList = rawPokemonList.filter((poke) => {
    if(text.input === "") {
      return poke;
    } else {
      return poke.name.toLowerCase().includes(text.input);
    }
  })

  const updatePokemonId = (idToChangeTo) => {
    rawPokemonList.map(poke => {
      if(poke.id == idToChangeTo) {
        setPokemon([idToChangeTo, poke]);
      }
    })
  }

  const updateTypesToDisplay = (e) => {
    if(typesToDisplay.length === 0) {
      const newTypesList = [e.target.value];
      setTypesToDisplay(newTypesList);
    } else {
      if(typesToDisplay.includes(e.target.value)) {
        const newTypesList = typesToDisplay.filter((type) => type !== e.target.value);
        setTypesToDisplay(newTypesList);
      } else {
        const newTypesList = [e.target.value];
        setTypesToDisplay(typesToDisplay.concat(newTypesList));
      }
    }
  }

  const resetTypesToDisplay = (e) => {

  }

  const RenderPokemonCards = () => {
    return (
      sortPokemonList.map((poke) => {
        let toRender = false;
        if(typesToDisplay.length === 0) {
          toRender = true;
        } else {
          poke.types.map((typeData) => {
            if(typesToDisplay.includes(typeData.type.name)) {
              toRender = true;
            }
          })
        }
        if(toRender) {
          return(
            <PokemonCard pokemonToUse={poke} updateIdCall={updatePokemonId}/>
          )
        } else {
          return (
            <></>
          )
        }
      })
    )
  }
  

  useEffect((i) => {
    const promises = [];
    for (let i = 1; i < 152; i++) {
      promises.push(getPokemonData(i));
    }
    Promise.all(promises).then((results)=>{
      setRawPokemonList(results);
    })
  }, []);

  return (
    <>
    <div className='content-parent'>
      <div className='card-container'>
        <RenderPokemonCards />
      </div>
      <div className='content-container'>
        {(pokemonId === 0) ? (
          <p>Click a pokemon!</p>
        ) : (
          <RenderPokemonInfo pokemon={pokemon} />
        )}
      </div>
    </div>
    <div className='type-container'>
      {pokemonTypeList.map((type) => (
        <div className='type-button' onClick={updateTypesToDisplay}>
          <PokemonTypeButton typeString={type} isButton={true}></PokemonTypeButton>
        </div>
      ))}
      <button> Reset! </button>
      <div>
        {typesToDisplay.map((type) => (
          <PokemonTypeButton typeString={type} glow={true}></PokemonTypeButton>
        ))}
      </div>
    </div>
    </>
  )
}

export default RenderPokemon;
