import React, { useState } from 'react';
import './App.css';
import RenderPokemonCards from './PokemonCards.js';
import RenderPokemonContent from './PokemonContent.js';

require('react-dom');
window.React2 = require('react');
console.log(window.React1 === window.React2);

const App = () => {
  const [inputString, setInputString] = useState("")
  const [pokemonId, setPokemonId] = useState(0);

  const updateInput = (e) => {
    const lowerString = e.target.value.toLowerCase();
    setInputString(lowerString);
  }

  const updatePokemonId = (e) => {
    setPokemonId(e.target.id);
  }

  return (
    <>
    <header className="header">
    </header>
    <div className='search-bar-container'>
      <img src='https://cdn-icons.flaticon.com/png/512/868/premium/868596.png?token=exp=1647880908~hmac=5e802c3c8b8423596716ca9353212d17' className='poke-ball-img' />
      <input type='text' placeholder='Search For a Pokemon!' onChange={updateInput}></input>
    </div>
    <div className='card-container'>
      <RenderPokemonCards input={inputString} />
    </div>
    <div className='poke-info'>
      <RenderPokemonContent input={pokemonId} />
    </div>
    </>
  );
}

export default App;
