import React, { useState, useEffect } from 'react';
import { cold } from 'react-hot-loader';
import { AnimatePresence } from 'framer-motion';
import './App.css';
import RenderPokemonCards from './RenderPokemon/RenderPokemon.js';
import PokemonTypeButton from './PokemonTypeButton/PokemonTypeButton.js';
import RenderPokemonInfo from './PokemonInfo/PokemonInfo.js'
import { getPokemonData } from './APIHandler';

const App = () => {
  const [inputString, setInputString] = useState("");
  const [rawPokemonList, setRawPokemonList] = useState([]);
  const [pokemonToRender, setPokemonToRender] = useState([]);
  const [pokemon, setPokemon] = useState([]);
  const pokemonTypeList = ['grass', 'poison', 'fire', 'flying', 'water', 'bug', 'normal', 'electric', 'ground', 'fairy', 'fighting', 'psychic', 'rock', 'steel', 'ice', 'ghost', 'dragon'];
  const [typesToDisplay, setTypesToDisplay] = useState([]);


  const deselectPokemon = () => {
    setPokemon([]);
  }

  useEffect((i) => {
    setRawPokemonList(getPokemonData());
    console.log(rawPokemonList)
  }, []);

  const updateInput = (e) => {
    const lowerString = e.target.value.toLowerCase();
    setInputString(lowerString);
  }

  const updatePokemon = (pokeToChangeTo) => {
    setPokemon(pokeToChangeTo);
  }

  const resetTypesToDisplay = (e) => {

  }

  const updateTypesToDisplay = (e) => {
    if(typesToDisplay.length === 0) {
      const newTypesList = [e.target.value];
      setTypesToDisplay(newTypesList);
    } else {
      if(typesToDisplay.includes(e.target.value)) {
        const newTypesList = typesToDisplay.filter((type) => type !== e.target.value);
        setTypesToDisplay(newTypesList);
      } else {
        const newTypesList = [e.target.value];
        setTypesToDisplay(typesToDisplay.concat(newTypesList));
      }
    }
  }

  return (
    <>
    <div className='sidebar-container'>
      <div className='search-bar-container'>
        <button onClick={() => deselectPokemon()}> Clear Poke</button> 
        <img src='https://cdn-icons.flaticon.com/png/512/868/premium/868596.png?token=exp=1647880908~hmac=5e802c3c8b8423596716ca9353212d17' className='poke-ball-img' />
        <input type='text' placeholder='Search For a Pokemon!' onChange={updateInput}></input>
      </div>
      <div className='card-container'>
        <RenderPokemonCards pokeList={rawPokemonList} searchInput={inputString} typesToDisplay={typesToDisplay} updatePokemon={updatePokemon}/>
      </div>
      <div className='type-container'>
        {pokemonTypeList.map((type) => (
          <div className='type-button' onClick={updateTypesToDisplay}>
            <PokemonTypeButton typeString={type} isButton={true}></PokemonTypeButton>
          </div>
        ))}
        <button> Reset! </button>
        <div>
          {typesToDisplay.map((type) => (
            <PokemonTypeButton typeString={type} isButton={false}></PokemonTypeButton>
          ))}
        </div>
      </div>
    </div>
    <div className='content-container'>
        {(pokemon.length === 0) ? (
          <p>Pick a pokemon!</p>
        ) : (
          <AnimatePresence exitBeforeEnter={true}>
            <RenderPokemonInfo pokemonToUse={pokemon} key={pokemon.name}/>
          </AnimatePresence>
        )}
    </div>
    </>
  );
}

export default cold(App);
