export const getPokemonData = async (id) => {

    const getData = async (id) => {
        try {
          const res = await fetch("https://pokeapi.co/api/v2/pokemon/" + id);
          const pokemonPromise = await res.json();
          return pokemonPromise;
        } catch (e) {
          console.log(e);
        }
      };

    const promises = [];
    for (let i = 1; i < 152; i++) {
      promises.push(getData(i));
    }
    Promise.all(promises).then((results)=>{
      setRawPokemonList(results);
    })
};


export const getAbilityData = async (abilityName) => {

    const getData = async (abilityName) => {
        try {
          const res = await fetch("https://pokeapi.co/api/v2/ability/" + abilityName);
          const pokemonPromise = await res.json();
          return pokemonPromise;
        } catch (e) {
          console.log(e);
        }
      };

    const promise = getData(abilityName);
    return Promise.all(promise);
};
