import React, { useState } from "react";
import { motion } from "framer-motion";

const RenderExpandableType = (props) => {

    const [expanded, setExpanded] = useState(false);

    const handleClick = () => {
      setExpanded(!expanded)
    }

    const type = props.typeToUse;
    const damageRelations = type.damage_relations;

    const typeExpand = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        collapsed: {
          y: '0',
          width: 300,
          height: 80,
          'overflow-y': 'hidden',
          opacity: 1,
        },
        expanded: {
          y: '0',
          width: 450,
          height: 800,
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }

    console.log(type)
    return (
      <motion.div className='matchup-button' variants={typeExpand} initial='before' animate={(expanded ? 'expanded' : 'collapsed')} exit='after' onClick={handleClick}>
        <h1>View {type.name} Damage Relations:</h1>
        {(damageRelations.double_damage_from.length === 0) ? (
            <></>
        ) : (
            <p>Double Damage From:</p>,
            React.Children.toArray(damageRelations.double_damage_from.map((damageData) => (
                <li>{damageData.name}</li>
            )))
        )}
        {(damageRelations.double_damage_to.length === 0) ? (
            <></>
        ) : (
            <p>Double Damage To:</p>,
            React.Children.toArray(damageRelations.double_damage_to.map((damageData) => (
                <li>{damageData.name}</li>
            )))
        )}
        {(damageRelations.half_damage_from.length === 0) ? (
            <></>
        ) : (
          <>
            <p>Half Damage From:</p>,
            {damageRelations.double_damage_from.map((damageData) => (
                <li key={damageData.name}>{damageData.name}</li>
            ))}
          </>
        )}
        {(damageRelations.half_damage_to.length === 0) ? (
            <></>
        ) : (
            <p>Half Damage To:</p>,
            React.Children.toArray(damageRelations.double_damage_from.map((damageData) => (
                console.log(damageData),
                <p>{damageData.name}</p>
            )))
        )}
        {(damageRelations.no_damage_from.length === 0) ? (
            <></>
        ) : (
            <p>No Damage From:</p>,
            React.Children.toArray(damageRelations.no_damage_from.map((damageData) => (
                <li>{damageData.name}</li>
            )))
        )}
        {(damageRelations.no_damage_to.length === 0) ? (
            <></>
        ) : (
            <p>No Damage To:</p>,
            React.Children.toArray(damageRelations.no_damage_from.map((damageData) => (
                <li>{damageData.name}</li>
            )))
        )}
      </motion.div>
    )
}

export default RenderExpandableType;
