import React, { useState } from 'react';
import './App.css';
import PokemonRender from './PokemonRender.js';
import handleSearch from './PokemonRender.js'

const App = () => {
  const [inputString, setInputString] = useState("")

  const updateInput = (e) => {
    const lowerString = e.target.value.toLowerCase();
    setInputString(lowerString);
  }

  return (
    <>
    <header className="header">
    </header>
    <div className="buttonContainer">
    </div>
    <div className='searchBarContainer'>
      <img src='https://cdn-icons.flaticon.com/png/512/868/premium/868596.png?token=exp=1647880908~hmac=5e802c3c8b8423596716ca9353212d17' className='pokeBallImg' />
      <input type='text' placeholder='Search For a Pokemon!' onChange={updateInput}></input>
    </div>
    <div className='cardContainer'>
      <PokemonRender input={inputString} />
    </div>
    </>
  );
}

export default App;
