import React, { useEffect, useState } from 'react';
import './RenderPokemon.css'
import PokemonCard from '../PokemonCard/PokemonCard.js'
import { motion } from 'framer-motion';

const RenderPokemonCards = (props) => {

  let typeBoolean = false;
  const sortPokemonList = props.pokeList.filter((poke) => {
    typeBoolean = false;
    poke.types.map((typeData) => {
      if(props.typesToDisplay.includes(typeData.type.name)) {
        typeBoolean = true;
      }
    })
    if(props.typesToDisplay.length === 0 || typeBoolean) {
      if(props.searchInput === "") {
        return poke;
      }
      return poke.name.toLowerCase().includes(props.searchInput);
    }
  })


  return (
    sortPokemonList.map((poke) => {
      return(
        <motion.div className='motion-card' whileTap={{scale: 0.8}}>
          <PokemonCard pokemonToUse={poke} updateCall={props.updatePokemon}/>
        </motion.div>
      )
    })
  )
}

export default RenderPokemonCards;
