import React from "react";
import { motion } from "framer-motion";
import { getAbilityName } from './PokemonAbilities.js'
import './PokemonAbilities.css'

const RenderPokemonAbilityBox = (ability) => {

    const boxAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
    }

    return (
        <motion.div className='ability-box' variants={boxAnim} initial='before' animate='onScreen' exit='before'>
            <p>{getAbilityName(ability.ability)}</p>
        </motion.div>
    )
}

export default RenderPokemonAbilityBox;
