import { motion } from "framer-motion"
import React from "react"
import './PokemonAbilities.css'

const getAbilityName = (ability) => {
    let name = "";
    ability.names.map((nameData) => {
        if(nameData.language.name === "en") {
            name = nameData.name;
        }
    })
    return name;
}

const RenderPokemonAbility = (abilityData) => {

    const buttonAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
          y: '-100vh',
          opacity: 0,
        }
    }

    const boxAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },

    }



    const effectEntries = abilityData.ability.effect_entries;
    return (
      <>
        <motion.div className='ability-button' variants={buttonAnim} initial='before' animate='onScreen' exit='after' whileTap={{scale: 0.8}}>
            {effectEntries.map((effectData) => (
                (effectData.language.name === "en") ? (
                    <>
                      <p>{getAbilityName(abilityData.ability)}</p>
                    </>
                ) : (
                    <></>
                )
            ))}
        </motion.div>

        <motion.div variants={boxAnim} initial='before' animate='onScreen' exit='before'>

        </motion.div>
      </>
    )
}

export default RenderPokemonAbility;
