import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import renderPokemon from './PokemonRendering';


const App = () => {
  const pokemonList = [];
//  const [pokemonData, setPokemonData] = useState([]);
//  const [pokemonType, setPokemonType] = useState("");


/*  function getPokemonData(id) {
    try{
      const pokeArray = [];
      const url = 'https://pokeapi.co/api/v2/pokemon/caterpie';// + id;
      const res = axios.get(url);
      pokeArray.push(res.data);
      console.log(pokeArray[0]);
      return pokeArray[0];
    } catch(e) {
      console.log('ERROR');
      console.log(e);
    }
  }*/

  const getPokemonData = async (id) => {
    try{
      const dataArray = [];
      const url = 'https://pokeapi.co/api/v2/pokemon/' + id;
      const res = await axios.get(url);
      dataArray.push(res.data);
      pokemonList.push(dataArray[0]);
      dataArray.length = 0;
    } catch(e) {
      console.log(e);
    }
  }

  useEffect((i) => {
    for(i = 1; i < 152; i++) {
      getPokemonData(i);
      renderPokemon();
    }
  }, []);

  return (
    <>
    <header className="header">
      <h1> Test 1 </h1>
    </header>
    <body className="App">
      <div className="Sidebar">
        <button className="button"> A-Z </button>
        <button className="button"> Catagorized </button>
      </div>
    </body>
    </>
  );
}

export function getPokemonData() {
  return(pokemonList)
}

export default App;
