import React from "react";
import PokeColour from "./TypeEnum.ts";


const getGradient = (props) => {
    console.log(props)
    const typeColour = PokeColour[props.typeString];
    const colourRGB = [typeColour.slice(1, 3), typeColour.slice(3, 5), typeColour.slice(5, 7)];
    const lighterColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 1.4))).join();
    const darkerColour = colourRGB.map(colour => (Math.round(parseInt(colour, 16) * 0.6))).join();

    const gradientString = 'rgb(' + lighterColour + ')' + ', rgb(' + darkerColour + ')';
    return gradientString;
}

export default getGradient;
