import React, { useState } from "react";
import { motion } from "framer-motion";
import './RenderExpandableGrowth.css';

const RenderExpandableGrowth = (props) => {

    const [expanded, setExpanded] = useState(false);

    const handleClick = () => {
        setExpanded(!expanded)
    }

    const growthData = props.growthToUse;
  

    const growthExpand = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        collapsed: {
          y: '0',
          width: 220,
          height: 80,
          'overflow-y': 'hidden',
          opacity: 1,
        },
        expanded: {
          y: '0',
          width: 350,
          height: 800,
          'overflow-y': 'scroll',
          opacity: 1,
        },
        after: {
          y: '100vh',
          opacity: 0,
        }
      }


    return (
      <motion.div className='growth-info-button' variants={growthExpand} initial='before' animate={(expanded ? 'expanded' : 'collapsed')} exit='after' onClick={handleClick}>
        <h1> View Levels </h1>
        {growthData.levels.map((levelData) => (
          <p>Level {levelData.level} - {levelData.experience} EXP</p>
        ))}
      </motion.div>
    )
}

export default RenderExpandableGrowth;
