import React, { useEffect, useState } from 'react';
import './PokemonRender.css'
import ReactDOM from 'react-dom';

const PokemonRender = (inputStr) => {
  const [pokemonList, setPokemonList] = useState([]);

  const getPokemonData = async (id) => {
    console.log(id);
    try {
      const res = await fetch("https://pokeapi.co/api/v2/pokemon/" + id);
      const pokemon = await res.json();
      if(pokemon.name.includes(inputStr)) {
        return pokemon;
      } else if(inputStr = ''){
        return pokemon;
      } else {
        return null;
      }
    } catch (e) {
      console.log(e);
    }
  };

  useEffect((i) => {
    const promises = [];
    for (let i = 1; i < 152; i++) {
      const returnedPokemon = getPokemonData(i);
      if(returnedPokemon != null) {
        promises.push(returnedPokemon);
      }
    }
    Promise.all(promises).then((results)=>{
      setPokemonList(results);
    })
    console.log(pokemonList)
  }, []);

  return (
      pokemonList.map(poke=>(
        <div className='card'>
          <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + poke.id + '.svg'} className='PokeImg' key={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + poke.id + '.svg'}/>
          <div className='container'>
            <p>{poke.name}</p>
          </div>
        </div>
      ))
  )
}

export default PokemonRender;
