import React from "react";
import { motion } from "framer-motion";
import { getAbilityName } from './PokemonAbilities.js'
import './PokemonAbilities.css'

const RenderPokemonAbilityBox = (ability) => {

    const boxAnim = {
        before: {
          y: '100vh',
          opacity: 0,
        },
        onScreen: {
          y: '0',
          opacity: 1,
        },
        after: {
            x: '100vh',
            opacity: 0,
        }
    }
    return (
        <motion.div className='ability-box' variants={boxAnim} initial='before' animate='onScreen' exit='after' key={ability}>
            <h1>{getAbilityName(ability.ability)}</h1>
            {ability.ability.effect_entries.map((effectData) => (
                (effectData.language.name === "en") ? (
                    <>
                      <p>{effectData.effect}</p>
                    </>
                  ) : (
                    <></>
                  )
            ))}
        </motion.div>
    )
}

export default RenderPokemonAbilityBox;
