import React, { useState, useEffect } from "react";
import PokemonTypeButton from '../PokemonTypeButton/PokemonTypeButton.js';
import RenderPokemonAbilityButton from "../PokemonAbilities/PokemonAbilities.js";
import RenderPokemonAbilityBox from "../PokemonAbilities/PokemonAbilityBox.js";
import './PokemonInfo.css'
import getGradient from "../GetGradient.js";
import { motion } from 'framer-motion';

const getAbilityData = async (abilityName) => {
  try {
    const res = await fetch("https://pokeapi.co/api/v2/ability/" + abilityName);
    const pokemonPromise = await res.json();
    return pokemonPromise;
  } catch (e) {
    console.log(e);
  }
};

const RenderPokemonInfo = (props) => {
    const [abilities, setAbilities] = useState([]);
    const [abilityToDisplay, setAbilityToDisplay] = useState([]);
    const pokemon = props.pokemonToUse;
    const pokemonCurrentTypes = [];
    let gradientString = "";
    
    useEffect((i) => {
      const promises = [];
      pokemon.abilities.map((abilityData) => {
        console.log(abilityData)
        promises.push(getAbilityData(abilityData.ability.name));
      })
      Promise.all(promises).then((results)=>{
        console.log(results)
        setAbilities(results);
      })
    }, []);

    const updateAbilityToDisplay = (ability) => {
      setAbilityToDisplay(ability);
    }

    const animateFromTop = {
      before: {
        y: '-100vh',
        opacity: 0,
      },
      onScreen: {
        y: '0',
        opacity: 1,
      },
      after: {
        y: '100vh',
        opacity: 0,
      }
    }

    pokemon.types.map((typeData) => {
        pokemonCurrentTypes.push(typeData.type.name);
    })

    if(pokemonCurrentTypes.length === 1) {
      gradientString = getGradient({typeString: pokemonCurrentTypes[0]});
    } else {
      gradientString = getGradient({typeString: pokemonCurrentTypes[0]}) + ', ' + getGradient({typeString: pokemonCurrentTypes[1]});
    }
    
    return (
      <>
        <motion.div style={{
          position: 'absolute',
          width: '420px',
          height: '640px',
          left: '75%',
          top: '30px',
          transform: 'translateX(-50%)',
          backgroundImage: 'linear-gradient(225deg, ' + gradientString + ')',
          boxShadow: '0 20px 20px 0 rgba(0,0,0,0.2)',
          borderRadius: '30px'}} key={pokemon.name} variants={animateFromTop} initial='before' animate='onScreen' exit='after'>
          <fieldset className="poke-height-fieldset">
            <legend style={{position: 'absolute', left: '10px'}}>{pokemon.height * 10} cm</legend>
            <div className='big-poke-img-container'>
              <img src={'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/' + pokemon.id + '.svg'} className='big-poke-img' key={pokemon.name}></img>
            </div>
          </fieldset>
          <div className='poke-type-container'>
              {pokemon.types.map((type) => React.Children.toArray(<PokemonTypeButton typeString={type.type.name} isButton={false}/>))}
          </div>
          <div className='ability-container'>
            {React.Children.toArray(abilities.map((abilityData) => (
              <RenderPokemonAbilityButton abilityData={abilityData} updateAbilityToDisplay={updateAbilityToDisplay()} />
            )))}
          </div>
        </motion.div>
        <div>
          {console.log(abilityToDisplay)}
          {(abilityToDisplay === []) ? (
              <></>
            ) : (
              <RenderPokemonAbilityBox ability={abilityToDisplay} />
            )}
        </div>
        <div className='stats-container'>
          <p>Stats:</p>
          {React.Children.toArray(pokemon.stats.map((statData) => (
            <p>{statData.stat.name} - {statData.base_stat}</p>
          )))}
        </div>

        <div className='experience-container'>
          <p>Base Exp - {pokemon.base_experience}</p>
        </div>

        <div className='weight-container'>
          <p>Weight - {pokemon.weight}</p>
        </div>
      </>
    )
}

export default RenderPokemonInfo;


