import React from "react";
import getGradient from "../GetGradient";

const PokemonTypeButton = (props) => {
  if(props.isButton) {
    return (
      <button style={{
        backgroundImage: "linear-gradient(120deg, " + getGradient({typeString: props.typeString}) + ")",
        borderRadius: "5px",
        height: '30px',
        width: '80px',
        textTransform: 'capitalize'}} value={props.typeString}>
          {props.typeString}
      </button>
    )
  } else {
    return (
      <p style={{
        backgroundImage: "linear-gradient(120deg, " + getGradient({typeString: props.typeString}) + ")",
        borderRadius: "5px",
        height: '30px',
        width: '80px',
        textTransform: 'capitalize'}} value={props.typeString}>
          {props.typeString}
      </p>
    )
  }
}

export default PokemonTypeButton;
